DELIMITER ;;
CREATE DEFINER=`refill`@`%` PROCEDURE IF NOT EXISTS `GetProductsByCodes`(IN productCodes VARCHAR(255))
BEGIN
    -- Declare a variable to hold the dynamic SQL query
    DECLARE sqlQuery TEXT;

    -- Construct the SQL query using the input parameter
    SET sqlQuery = CONCAT('SELECT p3.code, p1.product_id AS product_head_id, p2.product_id AS product_group_id, p3.product_id AS product_id,
     JSON_UNQUOTE(JSON_EXTRACT(p3.data, \'$.PRODUCT_GROUP_TYPE\')) AS product_group_type 
     FROM product p3 JOIN product p2 ON p2.name = SUBSTRING_INDEX(SUBSTRING_INDEX(p3.path, \'/\', -3), \'/\', 1) JOIN product p1 ON p1.name = SUBSTRING_INDEX(SUBSTRING_INDEX(p3.path, \'/\', -4), \'/\', 1) WHERE p3.code IN (', productCodes, ')');

    -- Prepare and execute the dynamic SQL query
    PREPARE stmt FROM sqlQuery;
    EXECUTE stmt;
    DEALLOCATE PREPARE stmt;
END;;
DELIMITER ;	

